Actionscript.script:
import flash.text.StaticText;
import flash.display.MovieClip;
import flash.events.Event;

area.addEventListener(MouseEvent.MOUSE_OVER, hover);
area.addEventListener(MouseEvent.MOUSE_OUT, mouseExit);
area.addEventListener(MouseEvent.MOUSE_DOWN, pressedDown);
area.addEventListener(MouseEvent.CLICK, clicked);

var mouseOver:Boolean = false
var connectedFunction: Function;

labelBut.autoSize = TextFieldAutoSize.CENTER;
labelBut.text = this.label;

glowLine.width = labelBut.width;
glowLine.visible = false;

this.gotoAndStop(2);

// Called when mouse hovers over the button
function hover(evt:Event): void {
	this.gotoAndPlay(5);
	mouseOver = true;
	Mouse.cursor = MouseCursor.BUTTON;
}

// Called when mouse exits the button
function mouseExit(evt:Event): void {
	mouseOver = false;
	Mouse.cursor = MouseCursor.AUTO;
	this.gotoAndStop(3);
}

// Called when button is pressed down
function pressedDown(evt:Event): void {
	this.gotoAndStop(4);
}

// Called when button is clicked (on release)
function clicked(evt:Event): void {
	connectedFunction();
	if (mouseOver)
		this.gotoAndPlay(5);
	else
		this.gotoAndStop(3);
}

function showGlowLine() {
	glowLine.visible = true;
}

function hideGlowLine() {
	glowLine.visible = false;
}

function disable(): void {
	this.gotoAndStop(4);
	area.removeEventListener(MouseEvent.MOUSE_OVER, hover);
	area.removeEventListener(MouseEvent.MOUSE_OUT, mouseExit);
	area.removeEventListener(MouseEvent.MOUSE_DOWN, pressedDown);
	area.removeEventListener(MouseEvent.CLICK, clicked);
}

function enable(): void {
	this.gotoAndStop(3);
	area.addEventListener(MouseEvent.MOUSE_OVER, hover);
	area.addEventListener(MouseEvent.MOUSE_OUT, mouseExit);
	area.addEventListener(MouseEvent.MOUSE_DOWN, pressedDown);
	area.addEventListener(MouseEvent.CLICK, clicked);
}

// Connect a function the button's click
function connect(funct: Function): void {
	this.connectedFunction = funct;
}

if (this.centre)
{
	area.x -= ((labelBut.width - area.width) / 2);
}
if (this.right)
{
	area.x += ((labelBut.width - area.width) / 2);
}
area.width = labelBut.width;
area.height = labelBut.height;
// Go to initial frame for button texture
this.gotoAndStop(3);
trace(labelBut.width);

if (this.centre_expand)
	labelBut.autoSize = TextFieldAutoSize.CENTER;
if (this.left_expand)
	labelBut.autoSize = TextFieldAutoSize.LEFT;
if (this.right_expand)
	labelBut.autoSize = TextFieldAutoSize.RIGHT;
if (this.none_expand)
	labelBut.autoSize = TextFieldAutoSize.NONE;
labelBut.text = this.label

if (this.centre_expand)
	labelBut.autoSize = TextFieldAutoSize.CENTER;
if (this.left_expand)
	labelBut.autoSize = TextFieldAutoSize.LEFT;
if (this.right_expand)
	labelBut.autoSize = TextFieldAutoSize.RIGHT;
if (this.none_expand)
	labelBut.autoSize = TextFieldAutoSize.NONE;
labelBut.text = this.label
